import { View, Text,StyleSheet,TouchableOpacity,Image, TextInput,} from 'react-native'
import React , {useState,useEffect} from 'react'
import { useNavigation } from '@react-navigation/native'
import { set } from 'date-fns';
import firebase from "../babel.config"
import { setStatusBarNetworkActivityIndicatorVisible } from 'expo-status-bar';

export default function AddList() {
    const navigation= useNavigation(); 
    const [activity,setActivity]=useState("")
    const [description,setDescription]=useState("");
    const [currentDate, setCurrentDate] = useState('');
    const [registerDate,setRegisterDate]=useState('')
    /// firebase connection
    const ref = firebase.firestore().collection("todos")
    const [data,setData]=useState("")
 
  useEffect(() => {
    var date = new Date().getDate(); //Current Date
    var month = new Date().getMonth() + 1; //Current Month
    var year = new Date().getFullYear(); //Current Year
    var hours = new Date().getHours(); //Current Hours
    var min = new Date().getMinutes(); //Current Minutes
    var sec = new Date().getSeconds(); //Current Seconds
    setCurrentDate(
      date + '/' + month + '/' + year 
    );


    setRegisterDate(
        date + '/' + month + '/' + year 
      + ' ' + hours + ':' + min + ':' + sec
      );
    
  }, []);
 ref.orderBy('createdAt', 'desc').onSnapshot(
    querySnapshot =>{
        const todos=[]
        querySnapshot.forEach((doc)=>{
            const {heading}=doc.data()
            todos.push({
                id:doc.id,
                heading,
            })
            setStatusBarNetworkActivityIndicatorVisible(todos)
        })
    }
 )
const addYourPlan=()=>{
if(activity===""|| description===""){
    alert("Please write your Activity and Description")
}else{
    setActivity(activity);
    setDescription(description);
    navigation.navigate('Make Your Day', {activity:activity,description:description,registerDate})
}
}
 
  return (

    <View style={styles.container}>
        <View style={styles.header}>
            <Image style={{width:100,height:100}} source={require('../assets/Image/day.png')}/>
            <View style={{marginRight:50}}>
             <Text style={{fontSize:30, fontWeight:'bold' }}>Make You Day</Text>
            </View>
        </View>
        <View style={{backgroundColor:'#E6F7F6', marginTop:20,width:400,height:500, borderRadius:5,alignItems:'center'}}>
            <View>
                <View style={{marginTop:10,backgroundColor:'white',width:380,height:40}}> 
                     <TextInput style={{width:380,height:40}} placeholder='Activity'  onChangeText={(activity)=>setActivity(activity)}/> 
                </View>
                <View style={{marginTop:10,backgroundColor:'white',width:380,height:200, borderRadius:10}}>
                     <TextInput style={{width:380,height:200}} placeholder='Description' multiline={true}
                 autoCorrect={true}
                 autoCapitalize={true}
                 onChangeText={(description)=>setDescription(description)}/>
                </View>
                <View style={{marginTop:10}}>
                     <Text>Today is : {currentDate}</Text>
                </View>

            </View>

          
           

              
            
         </View>
        
        <View>
            <TouchableOpacity style={styles.button}
                  onPress={()=>{addYourPlan(); }}>
                 <Text style={{fontSize:20,fontWeight:'bold'}}>Add Your Plan</Text>
            </TouchableOpacity>
        </View>
    </View>
  )
}


const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
  
    },
    header:{
        marginTop:30,
        backgroundColor:'#7DECF3',
        height:100,
        width:400,
        borderRadius:10,
        alignItems:'center',
        flexDirection:'row',
        justifyContent:'space-between'

    },
    button:{
        marginTop:30, 
        backgroundColor:'#7DECF3', 
        padding:20,
        borderRadius:50}
  });
